import { useState } from "react";
const tasks = [
  {
    id: 1,
    text: 'Doctor Appointment',
    day: 'Feb 5th at 2:30 pm',
    reminder: true
  },
  {
    id: 2,
    text: 'Doctor Appointment',
    day: 'Feb 5th at 2:30 pm',
    reminder: true
  }
]
const Tasks = props => {
  
  return (
    <>
    {
      tasks.map((task) => (
        <h3 key={task.id}>{task.text}</h3>
      ))}
      
    </>
  )
}

Tasks.propTypes = {

}

export default Tasks
