import PropTypes from 'prop-types';

const Header = ({ title }) => {
  return (
    <header>
      <h1>{title}</h1> 
    </header>
  )
}
// for styling add style = {headingStyle} in h1 tag

Header.defaultProps = {
  title: 'Task Tracker'
}

Header.propTypes = {
  title: PropTypes.string.isRequired
}

// css in js
const  headingStyle = {
  color: 'red',
  backgroundColor: 'black'  
}

export default Header
